package com.bipin.practice.hackerearrth._2019;

import java.math.BigDecimal;

public class Airbus_24Nov {

	/**
	 * select * from limit; with t as ( select name from transactions where
	 * AMOUNT > (select nvl(min(perday),0) from limit) union select name from
	 * transactions group by name having sum(amount) > (select
	 * nvl(min(perweek),0) from limit)) select name from t union select 'none'
	 * from dual where not exists (select 1 from t) ;
	 * 
	 * @param args
	 */
	public static void main(String[] args) {// main power of numbers
		getfreq("");
	}
	static int getfreq(String octal) {
		// convert to decimal
		String s = "";
		BigDecimal num1 = new BigDecimal(s);
		System.out.println(s.length());
		BigDecimal num2 = convertDecimalToOctal(num1);
		System.out.println(num2);
		System.out.println((convertOctalToDecimal(num2) + "").equals(s));
		return 0;
	}
	static BigDecimal convertOctalToDecimal(BigDecimal octal) {
		BigDecimal decimalNumber = new BigDecimal(0);
		int i = 0;
		BigDecimal zero = new BigDecimal(0);
		BigDecimal ten = new BigDecimal(10);
		BigDecimal eight = new BigDecimal(8);
		while (!octal.equals(zero)) {
			BigDecimal rem = octal.remainder(ten);
			BigDecimal pow = eight.pow(i);
			BigDecimal res = rem.multiply(pow);
			decimalNumber = decimalNumber.add(res);
			++i;
			octal = octal.divide(ten, ten.ROUND_FLOOR);
		}
		return decimalNumber;
	}
	static BigDecimal convertDecimalToOctal(BigDecimal decimal) {
		BigDecimal octalNumber = new BigDecimal(0);
		BigDecimal i = new BigDecimal(1);
		BigDecimal zero = new BigDecimal(0);
		BigDecimal ten = new BigDecimal(10);
		BigDecimal eight = new BigDecimal(8);
		while (!decimal.equals(zero)) {
			BigDecimal rem = decimal.remainder(eight);
			rem = rem.multiply(i);
			octalNumber = octalNumber.add(rem);
			decimal = decimal.divide(eight, eight.ROUND_FLOOR);
			i = i.multiply(ten);
		}
		return octalNumber;
	}
}
